DROP SCHEMA Rhosegold;
CREATE SCHEMA Rhosegold;
USE Rhosegold;

CREATE TABLE Brukere (
Epost CHAR(100),
Passord CHAR(100),
Adresse VARCHAR(100),
Add_Adresse VARCHAR(100),
Navn VARCHAR(100),
Postnr VARCHAR(4),
Poststed VARCHAR(100)
);

CREATE TABLE Kategorier (
KategoriNavn VARCHAR(50) NOT NULL PRIMARY KEY
);


CREATE TABLE Produkter (
ProduktNavn VARCHAR(30),
ProduktId CHAR(6) PRIMARY KEY,
Beskrivelse VARCHAR(2000),
Ingredienser VARCHAR(2000),
Pris DECIMAL,
Kategori VARCHAR(50)
);



